import Axios from 'axios';
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom';
import { Row, Col } from 'react-bootstrap';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Helmet } from 'react-helmet-async';
import { React, useContext, useEffect, useState } from 'react';
import { Store } from '../Store';
import { toast } from 'react-toastify';
import { getError } from '../utils';

const reducer = (state, action) => {
  switch (action.type) {
    case 'USER_RESET_PASSWORD_REQUEST':
      return { loading: true };

    case 'USER_RESET_PASSWORD_SUCCESS':
      return { loading: false, message: action.payload.message };

    case 'USER_RESET_PASSWORD_FAIL':
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export default function ResetPasswordScreen() {
  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';

  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const submitHandler = async (e) => {
    e.preventDefault();
     //const params = useParams();
     const token = useParams.params.token
    try {
      if (password !== confirmPassword) {
        toast.error(getError('Les motes de passe ne sont pas indentiques'))
      }
      else {

      const { data } = await Axios.patch(`/api/users/resetpassword/${token}`,{ password });

      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate(redirect || '/');
    }} catch (err) {
      toast.error(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container className="small-container">
      <Helmet>
        <title>réinitialiser le mot de passe</title>
      </Helmet>
      <h1 className="my-3">réinitialiser le mot de passe</h1>
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Nouveau mot de passe</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Comfirmer votre nouveau mot de passe</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Enregistrer</Button>
        </div>     
        <Row className="py-3 d-block">
        <Col className="my-2">
          {' '}
          <Link to={`/signin?redirect=${redirect}`}>
            Se connecter
          </Link>
        </Col>
      </Row>
      </Form>
    </Container>
  );
}
