import Axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Helmet } from 'react-helmet-async';
import { useContext, useEffect, useState, useReducer } from 'react';
import { Store } from '../Store';
import { toast } from 'react-toastify';
import { getError } from '../utils';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { useSelector } from 'react-redux'

const reducer = (state, action) => {
  switch (action.type) {
    case 'USER_FORGOT_PASSWORD_REQUEST':
      return { loading: true };

    case 'USER_FORGOT_PASSWORD_SUCCESS':
      return { loading: false, message: action.payload.message };

    case 'USER_FORGOT_PASSWORD_FAIL':
      return { loading: false, error: action.payload };

    case 'USER_RESET_PASSWORD_REQUEST':
      return { loading: true };

    case 'USER_RESET_PASSWORD_SUCCESS':
      return { loading: false, message: action.payload.message };

    case 'USER_RESET_PASSWORD_FAIL':
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

export default function ForgotPasswordScreen() {

  const [{ loading, message ,error}, dispatch] =
  useReducer(reducer, {
    loading: true,
    error: '',

  });
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await Axios.get(`/api/products/slug/${slug}`);
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: getError(err) });
      }
    };
    fetchData();
  }, [slug]);

  const navigate = useNavigate();
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';

  const [email, setEmail] = useState('');

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await Axios.post('/api/users/forgotpassword', {
        email,
      });
      ctxDispatch({ type: 'USER_SIGNIN', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate(redirect || '/');
    } catch (err) {
      toast.error(getError(err));
    }
  };

  useEffect(() => {
    if (userInfo) {
      navigate(redirect);
    }
  }, [navigate, redirect, userInfo]);

  return (
    <Container className="small-container">
      <Helmet>
        <title>Assistance du mot de passe</title>
      </Helmet>
      <h1 className="my-3">Assistance du mot de passe</h1>
      <p>Saisissez l'adresse e-mail associée à votre compte.</p>
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      {message && <MessageBox variant="success">{message}</MessageBox>}
      {loading && <LoadingBox />}

      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Continuer</Button>
        </div>     
      </Form>
    </Container>
  );
}
